void MotorPhase(signed char num, float val) {
	val = val * (float)PWM_MAX;
	num = num % 6;
	if(num < 0) {
		num += 6;
	}
	switch(num) {
		case 0:
			// W - NC
			PDC1 = 0;
			PDC7 = PWM_MAX;
			// V - V+
			PDC2 = val;
			PDC8 = val;
			// U - GND
			PDC3 = 0;
			PDC9 = 0;
			break;
		case 1:
			// W - V+
			PDC1 = val;
			PDC7 = val;
			// V - NC
			PDC2 = 0;
			PDC8 = PWM_MAX;
			// U - GND
			PDC3 = 0;
			PDC9 = 0;
			break;
		case 2:
			// W - V+
			PDC1 = val;
			PDC7 = val;
			// V - GND
			PDC2 = 0;
			PDC8 = 0;
			// U - NC
			PDC3 = 0;
			PDC9 = PWM_MAX;
			break;
		case 3:
			// W - NC
			PDC1 = 0;
			PDC7 = PWM_MAX;
			// V - GND
			PDC2 = 0;
			PDC8 = 0;
			// U - V+
			PDC3 = val;
			PDC9 = val;
			break;
		case 4:
			// W - GND
			PDC1 = 0;
			PDC7 = 0;
			// V - NC
			PDC2 = 0;
			PDC8 = PWM_MAX;
			// U - V+
			PDC3 = val;
			PDC9 = val;
			break;
		case 5:
			// W - GND
			PDC1 = 0;
			PDC7 = 0;
			// V - V+
			PDC2 = val;
			PDC8 = val;
			// U - NC
			PDC3 = 0;
			PDC9 = PWM_MAX;
			break;       
	}
}

bool bemf_phase(unsigned char phase) {
	phase = phase % 6;
	if(phase == 0) {
		return comp_w;
	} else if(phase == 1) {
		return !comp_v;
	} else if(phase == 2) {
		return comp_u;
	} else if(phase == 3) {
		return !comp_w;
	} else if(phase == 4) {
		return comp_v;
	} else if(phase == 5) {
		return !comp_u;
	}
	return false;
}

